const rows = $input.all();
const today = new Date();

function parseDate(value) {
  if (!value) return null;

  // If Excel serial number (integer days since 1899-12-30)
  if (typeof value === 'number') {
    return new Date((value - 25569) * 86400 * 1000);
  }

  // If already a JS Date
  if (value instanceof Date && !isNaN(value)) {
    return value;
  }

  // Convert dd/mm/yyyy to JS Date
  if (typeof value === 'string') {
    const parts = value.split('/');
    if (parts.length === 3) {
      const [day, month, year] = parts;
      return new Date(year, month - 1, day);
    }
  }

  return null;
}

const results = rows.map(item => {
  const dateString = item.json["Date"];
  const dateValue = parseDate(dateString);

  const diffMonths =
    (today.getFullYear() - dateValue.getFullYear()) * 12 +
    (today.getMonth() - dateValue.getMonth());

  let status = "";

  if (today > dateValue) {
    status = "périmé";
  } else if (diffMonths >= 12) {
    status = "12 months";
  } else if (diffMonths >= 9) {
    status = "9 months";
  } else if (diffMonths >= 6) {
    status = "6 months";
  } else if (diffMonths >= 3) {
    status = "3 months";
  } else {
    //status = `still ${(-1)* diffMonths} months`;
    if(diffMonths == 0){ status= 'this months' }
    if(diffMonths<0 && diffMonths>=(-3)){status = 'bi3ny t7awa'}
    if(diffMonths<(-3) && diffMonths>=(-6)){status = 'mazal 6 mois'}
    if(diffMonths<(-6) && diffMonths>=(-9)){status = 'mazal 9 mois'}
    if(diffMonths<(-9) && diffMonths>=(-12)){status = 'mazal 12 mois'}
    if(diffMonths <(-12)){status = 'ghayarha mazal 3lia l7al'}
  }

  return {
    json: {
      Date: dateString,
      Difference: diffMonths,
      status: status
    }
  };
});

return results;
